import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:stop_and_shop/Screens/Locations/Locations.dart';import 'package:stop_and_shop/modules/module_cart.dart';import 'package:stop_and_shop/style/colors.dart';import '../../shared/components/components.dart';class CheckOut extends StatelessWidget {  const CheckOut({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Directionality(      textDirection: TextDirection.rtl,      child: Scaffold(        backgroundColor: Colors.white,        appBar: AppBar(          backgroundColor: Colors.white,          elevation: 0,          titleSpacing: 20,          title: TitleName(title: 'الدفع'),          leading: TextButton(            onPressed: () {              Get.back();            },            child: const Icon(              Icons.arrow_back_ios,              color: Colors.black,            ),          ),        ),        body: Container(          width: double.infinity,          padding: const EdgeInsets.all(10),          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Row(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  buildText1(text: 'سعر المنتجات'),                  buildText2(text: '${CartModule.totalPrice()}')                ],              ),              Row(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  buildText1(text: 'سعر التوصيل'),                  buildText2(text: '0.0')                ],              ),              Row(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [buildText1(text: 'الخصم'), buildText2(text: '0.0')],              ),              Divider(                color: greenColor,                thickness: 1,              ),              Row(      