import 'package:flutter/material.dart';class MyTextField extends StatelessWidget {  const MyTextField({    Key? key,    required this.label,    required this.controller,    this.passKey = false,  }) : super(key: key);  final String label;  final bool passKey;  final TextEditingController controller;  @override  Widget build(BuildContext context) {    return TextField(      obscureText: passKey,      controller: controller,      style: const TextStyle(fontWeight: FontWeight.bold),      decoration: InputDecoration(        labelText: label,        border: OutlineInputBorder(          borderSide: BorderSide.none,          borderRadius: BorderRadius.circular(12),        ),      ),    );  }}class MyPasswordField extends StatelessWidget {  const MyPasswordField({    Key? key,    required this.label,    required this.controller,  }) : super(key: key);  final String label;  final TextEditingController controller;  @override  Widget build(BuildContext context) {    return TextFormField(      validator: (String? value) {        if(value!.isEmpty){          return 'هذا الحقل مطلوب';        }      },      obscureText: true,      controller: controller,      style: const TextStyle(fontWeight: FontWeight.bold),      decoration: InputDecoration(        labelText: label,        border: OutlineInputBorder(          borderSide: BorderSide.none,          borderRadius: BorderRadius.circular(12),        ),      ),    );  }}class MainContainer extends StatelessWidget {  const MainContainer({    Key? key,    required this.height,    required this.child,  }) : super(key: key);  final double height;  final Widget child;  @override  Widget build(BuildContext context) {    return Container(      height: height,      margin: const EdgeInsets.only(right: 16, left: 16),      // padding: const EdgeInsets.all(16),      color: Colors.white,      child: child,    );  }}