import 'package:get/get.dart';import 'module_product.dart';class FavoriteModule {  final RxString nameProduct;  final RxString imageProduct;  final RxString priceProduct;  final RxString categoryProduct;  final Product product;  FavoriteModule({    required String nameProduct,    required String imageProduct,    required String categoryProduct,    required String priceProduct,  })  : nameProduct = nameProduct.obs,        imageProduct = imageProduct.obs,        priceProduct = priceProduct.obs,        categoryProduct = categoryProduct.obs,        product = Product.products            .firstWhere((e) => e.nameProduct.value == nameProduct);  static add(String nameProduct, String imageProduct, String categoryProduct,      String priceProduct) {    FavoriteModule favorite = FavoriteModule(      nameProduct: nameProduct,      imageProduct: imageProduct,      categoryProduct: categoryProduct,      priceProduct: priceProduct,    );    favorites.add(favorite);  }  static RxList<FavoriteModule> favorites = <FavoriteModule>[].obs;}