import 'package:get/get.dart';class LocationModule {  final RxString nameLocation;  final RxString location;  LocationModule({    required String nameLocation,    required String location,  })  : nameLocation = nameLocation.obs,        location = location.obs;  static add(String nameLocation, String location) {    LocationModule location1 =        LocationModule(nameLocation: nameLocation, location: location);    locations.add(location1);  }  static RxList<LocationModule> locations = <LocationModule>[].obs;}