import 'package:get/get.dart';class User {  final RxString email;  final RxString password;  final RxString fullName;  final RxString? phoneNumber;  User(    String phoneNumber, {    required String email,    required String password,    required String fullName,  })  : email = email.obs,        password = password.obs,        fullName = fullName.obs,        phoneNumber = phoneNumber.obs;  static add(      String email, String password, String fullName, String? phoneNumber) {    User user = User(      phoneNumber!,      email: email,      password: password,      fullName: fullName,    );    users.add(user);  }  static update(      String email, String password, String fullName, String? phoneNumber) {    User user = User(      phoneNumber!,      email: email,      password: password,      fullName: fullName,    );    users[users.indexWhere((e) => e.email.value == email)] = user;  }  static RxList<User> users = <User>[].obs;}